mapper.select:
SELECT
FROM l_user U INNER JOIN l_user_role UR ON U.ID = UR.USER_ID INNER JOIN l_role R ON UR.ROLE_ID = R.ID WHERE UR.IS_ENABLE = 1 AND UR.IS_DELETE = 0 AND U.IS_ENABLE = 1 AND U.IS_DELETE = 0 AND R.IS_ENABLE = 1 AND R.IS_DELETE = 0
AND U.ID = #{user.id}

select lr.ID as roleId, lr.ROLE_NAME as roleName, lr.DESCRIPTION from l_role lr where lr.is_enable = 1 and lr.is_delete = 0
and lr.label = #{label}
and lr.role_name like #{searchName}
order by lr.create_time desc
