mapper.select:
SELECT

FROM l_user
WHERE l_user.IS_DELETE = 0

    AND ID = #{user.id}


    AND LOGIN_NAME = #{user.loginName}

select lu.id as userId, lu.REAL_NAME as userRealName, lu.LOGIN_NAME as userLoginName, lu.TEL as userTel, lu.AVATAR as userAvatar, lu.MOBIL as userMobil, lu.EMAIL as userEmail, lch.id as householdId, lch.HOUSEHOLD_NUMBER as householdName, lcl.id as layerId, lcl.LAYER_NUMBER as layerName, lcu.id as unitId, lcu.UNIT_NUMBER as unitName, lcb.id as buildingId, lcb.BUILDING_NUMBER as buildingName, lcp.id as periodId, lcp.PERIOD_NUMBER as periodName from l_user lu left join l_community_household_user lchu on lu.ID = lchu.USER_ID left join l_community_household lch on lchu.HOUSEHOLD_ID = lch.ID left join l_community_layer lcl on lch.LAYER_ID = lcl.ID left join l_community_unit lcu on lcl.UNIT_ID = lcu.ID left join l_community_building lcb on lcu.BUILDING_ID = lcb.ID left join l_community_period lcp on lcb.PERIOD_ID = lcp.ID where lu.IS_DELETE = 0
AND lu.ID = #{userId}
and ( lu.REAL_NAME like #{search} or lu.NICK_NAME like #{search} or lu.LOGIN_NAME like #{search} or lu.MOBIL like #{search} )
AND lu.IS_ENABLE = #{isEnableUser}
and lchu.id is null
AND lchu.IS_ENABLE = #{isEnableHouseHold} AND lchu.IS_DELETE = 0
AND lcp.ID in
#{periodId}
