mapper.select:
select u.ID as userId, u.REAL_NAME as userName, u.MOBIL as userMobile, u.AVATAR as userAvatar, e.id, e.EXCEPTION_TYPE, e.EXCEPTION_DESCRIPTION, e.IS_DEAL_WITH, e.RESULT, e.CREATE_TIME, e.UPDATE_TIME, u2.REAL_NAME as updateUserName, u2.MOBIL as updateUserMobile from l_community_exception e inner join l_user u on e.CREATE_USER = u.id left join l_user u2 on e.UPDATE_USER = u2.id where e.IS_DELETE = 0 and e.IS_ENABLE = 1
and u.REAL_NAME like #{userNameLike}
and e.IS_DEAL_WITH = #{status}
and e.EXCEPTION_TYPE = #{exceptionType}
and ( u.period_id in
#{periodId}
or u.period_id is null )
order by e.create_time desc
order by e.update_time desc
