mapper.update:
update l_user
set PERIOD_ID = #{periodId}
where id = #{userId}

mapper.select:
select lu.id as userId, lu.REAL_NAME as userRealName, lu.LOGIN_NAME as userLoginName, lu.TEL as userTel, lu.AVATAR as userAvatar, lu.MOBIL as userMobil, lu.EMAIL as userEmail, lch.id as householdId, lch.HOUSEHOLD_NUMBER as householdName, lcl.id as layerId, lcl.LAYER_NUMBER as layerName, lcu.id as unitId, lcu.UNIT_NUMBER as unitName, lcb.id as buildingId, lcb.BUILDING_NUMBER as buildingName, lcp.id as periodId, lcp.PERIOD_NUMBER as periodName from l_community_household_user as chu inner join l_user lu on chu.USER_ID = lu.ID inner join l_community_household lch on chu.HOUSEHOLD_ID = lch.ID inner join l_community_layer lcl on lch.LAYER_ID = lcl.ID inner join l_community_unit lcu on lcl.UNIT_ID = lcu.ID inner join l_community_building lcb on lcu.BUILDING_ID = lcb.ID inner join l_community_period lcp on lcb.PERIOD_ID = lcp.ID where chu.IS_DELETE = 0 and chu.IS_ENABLE = 0
AND lcp.ID in
#{periodId}
and chu.USER_ID = #{userId}
and chu.HOUSEHOLD_ID = #{householdId}
and ( lu.MOBIL = #{fuzzySearch} or lu.LOGIN_NAME = #{fuzzySearch} or lu.REAL_NAME = #{fuzzySearch} or lu.NICK_NAME = #{fuzzySearch} )
and lcl.ID = #{layerId}
and lcu.ID = #{unitId}
and lcb.ID = #{buildingId}
and lcp.ID = #{periodId}
and lu.REAL_NAME like #{userNameLike}
order by lu.CREATE_TIME desc
